import React from 'react';
import { Link } from 'react-router-dom';

import './styles/CharacterDetails.css';
import confLogo from '../images/platziconf-logo.svg';
import Character from '../components/Character';
import DeleteCharacterModal from '../components/DeleteCharacterModal';

function CharacterDetails(props) {
  const character = props.character;

  return (
    <div>
      <div className="CharacterDetails__hero">
        <div className="container">
          <div className="row">
            <div className="col-6">
              <img src={confLogo} alt="Logo de la Conferencia" />
            </div>
            <div className="col-6 CharacterDetails__hero-attendant-name">
              <h1>
                {character.firstName} {character.lastName}
              </h1>
            </div>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="row">
          <div className="col">
            <Character
              firstName={character.firstName}
              lastName={character.lastName}
              email={character.email}
              twitter={character.twitter}
              jobTitle={character.jobTitle}
            />
          </div>
          <div className="col">
            <h2>Actions</h2>
            <div>
              <div>
                <Link
                  className="btn btn-primary mb-4"
                  to={`/characters/${character.id}/edit`}
                >
                  Edit
                </Link>
              </div>

              <div>
                <button onClick={props.onOpenModal} className="btn btn-danger">
                  Delete
                </button>
                <DeleteCharacterModal
                  isOpen={props.modalIsOpen}
                  onClose={props.onCloseModal}
                  onDeleteCharacter={props.onDeleteCharacter}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CharacterDetails;
